import React from 'react';
import Search from "./Search";
import PanelAdd from "./PanelAdd";
import './Menu.css'


class Menu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {newItemPanel: false};
        this.add = this.add.bind(this); // para cada elemento basado en evento
    }

    add() {
        this.setState({newItemPanel: true});
        console.log("Esto esta finooo")
    }

    onCalcel(e){
        e.preventDefault();
        this.setState({newItemPanel: false});
    }



    render() {


        return (
            <div className="container">
                <div className="subcontainer">

                    <div className="logo">
                        {this.props.title}
                    </div>
                    <div className="search">
                        <Search/>
                    </div>
                    <div className="actions">
                        <button onClick={this.add} className="button btn-blue">+ AÃ±adir nuevo Libro</button>
                    </div>
                </div>
                {
                    (this.state.newItemPanel)? <PanelAdd onCancel={this.onCalcel}/> : ''
                }


            </div>
        );

    }

}
export default Menu;
